import { useState } from 'react';
import { Col, Image } from 'react-bootstrap';
import { useSelector } from 'react-redux';
import CloseButtonImage from './cross button.png'; // Replace with your close button image

export const UserProfile = (user) => {
  const [isHovered, setIsHovered] = useState(false);
  const colWidthUser = useSelector((store) => store.user.colWidthUser);

  const handleMouseEnter = () => {
    setIsHovered(true);
  };

  const handleMouseLeave = () => {
    setIsHovered(false);
  };

  const base64String = btoa(
    String.fromCharCode(...new Uint8Array(user.image.data.data))
  );

  return (
    <Col
      xs={colWidthUser}
      className={isHovered ? 'user-profile-hovered' : 'user-profile'}
      onMouseEnter={handleMouseEnter}
      onMouseLeave={handleMouseLeave}
    >
      {isHovered && (
        <img
          className="close-button"
          src={CloseButtonImage}
          alt="Close"
          onClick={() => {
            // Handle close button click event
          }}
        />
      )}
      <div className="divContainerUser">
        <Image
          className="userImage"
          src={`data:image/png;base64,${base64String}`}
          alt={user.name}
          roundedCircle
        />
      </div>
      <h5>{user.name}</h5>
    </Col>
  );
};
