import { createSlice } from '@reduxjs/toolkit'
import { userInfo } from '../../userInfo'

const initialState = {
    userInfo: userInfo,
    showForm: false,
}

export const userSlice = createSlice({
    name: 'user',
    initialState,
    reducers: {
        addUserInfo: (state,action) => {
            console.log(state.userInfo.length)
            // const {name, path} = action.payload;
            // const newUser = {
            //     id: (state.userInfo.length + 1).toString(),
            //     name: name,
            //     path: path,
            // }
        },
        openForm: (state) => {
            state.showForm = true;
        },
        closeForm: (state) => {
            state.showForm = false;
        },
    },
})

export const { addUserInfo, openForm, closeForm } = userSlice.actions
export default userSlice.reducer
