import { useEffect } from 'react';
import Col from 'react-bootstrap/Col';
import Container from 'react-bootstrap/Container';
import Image from 'react-bootstrap/Image';
import Row from 'react-bootstrap/Row';
import { useDispatch, useSelector } from 'react-redux';
import { addUserInfo } from '../features/user/userSlice';
import { Button } from 'react-bootstrap';


export function MainContainer() {
    const { userInfo } = useSelector((store) => store.user)

    const dispatch = useDispatch();
    useEffect(() => {
        dispatch(intiateUserInfo())
    }, [userInfo])
    return (
        <Container>
            <Row>
                {userInfo.map((user) => (
                    <Col key={user.id} xs={6} md={4} style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
                        <div className='userButton'>
                            <Image
                                src={user.path}
                                alt={user.name}
                                roundedCircle
                                style={{ position: 'absolute', top: 0, left: 0, width: '100%', height: '100%' }}
                            />
                        </div>
                    </Col>
                ))}
                <Col xs={6} md={4} style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
                        <Button style={{ width: '25vw', height: '25vw', position: 'relative' }} variant="primary">
                            <Image style={{ width: '25vw', height: '25vw', position: 'relative' }} src="AddButton.png" alt="Add Button" roundedCircle />
                            Add User
                        </Button>
                </Col>
            </Row>
        </Container>
    )
}
