import { useEffect } from 'react';
import Col from 'react-bootstrap/Col';
import Container from 'react-bootstrap/Container';
import Image from 'react-bootstrap/Image';
import Row from 'react-bootstrap/Row';
import { useDispatch, useSelector } from 'react-redux';
import { addUserInfo } from '../features/user/userSlice';
import { Button, Stack } from 'react-bootstrap';
import '../styles.css'


export function MainContainer() {
    const { userInfo } = useSelector((store) => store.user)
    return (
        <Container>
            <Row>
                {userInfo.map((user) => (
                    <Stack gap={2}>
                    <Col className='userImageContainerColumn' key={user.id} xs={6} md={4}>
                        <div className='userImageContainer'>
                            <Image
                                className='userImage'
                                src={user.path}
                                alt={user.name}
                                roundedCircle
                            />
                        </div>
                    </Col>
                    </Stack>
                ))}
                <Col className='userImageContainerColumn' xs={6} md={4}>
                    <Button className='userAddImageContainer' variant="transparent">
                        <Image
                            className='userAddImage'
                            src="AddButton.png"
                            alt="Add Button"
                            roundedCircle
                        />
                    </Button>
                </Col>
            </Row>
        </Container>
    )
}
