import { useEffect } from 'react';
import Col from 'react-bootstrap/Col';
import Container from 'react-bootstrap/Container';
import Image from 'react-bootstrap/Image';
import Row from 'react-bootstrap/Row';
import { useDispatch, useSelector } from 'react-redux';
import { addUserInfo } from '../features/user/userSlice';
import { Button } from 'react-bootstrap';
import '../styles.css';

export function MainContainer() {
  const { userInfo } = useSelector((store) => store.user);

  const renderUserColumns = () => {
    const columns = userInfo.map((user) => (
      <Col className='userImageContainerColumn' key={user.id} xs={4} md={4}>
        <div className='userImageContainer'>
          <Image
            className='userImage'
            src={user.path}
            alt={user.name}
            roundedCircle
          />
        </div>
      </Col>
    ));

    const totalColumns = columns.length;
    const rows = [];
    const maxColumnsPerRow = 3;

    for (let i = 0; i < totalColumns; i += maxColumnsPerRow) {
      const row = (
        <Row key={i}>
          {columns.slice(i, i + maxColumnsPerRow)}
        </Row>
      );

      // Add the 'Add Button' on the second row, third column if there are 5 images
      if (totalColumns === 5 && i === maxColumnsPerRow) {
        row.props.children.push(
          <Col className='userImageContainerColumn' key={totalColumns}>
            <Button className='userAddImageContainer' variant="transparent">
              <Image
                className='userAddImage'
                src="AddButton.png"
                alt="Add Button"
                roundedCircle
              />
            </Button>
          </Col>
        );
      }

      rows.push(row);
    }

    return rows;
  };

  return (
    <Container>
      {renderUserColumns()}
    </Container>
  );
}
