import { Accordion, Button, Col, Container, Row } from "react-bootstrap";
import { useLocation, useParams } from "react-router-dom";
import { AddButton } from "./AddButton";
import { useDispatch, useSelector } from "react-redux";
import { useEffect } from "react";
import axios from "axios";
import { initiliaseTransaction } from "../features/transaction/transactionSlice";

export const UserTransaction = () => {
    // const location = useLocation();
    // const user = location.state || {}; 
    // const { name, customId } = user;
    const { selectedUserInfo } = useSelector((store) => store.user);
    const { transaction } = useSelector((store) => store.transaction);
    const dispatch = useDispatch();

    const getData = async () => {
        try {
            const res = await axios.get('http://localhost:5000/userTransaction')
            const transaction = res.data.data.map(({
                userName, userNameCustomId, customId, title, category, type, currency, amount, description,
            }) => ({
                userName, userNameCustomId, customId, title, category, type, currency, amount, description,
            }));
            dispatch(initiliaseTransaction(transaction));
        } catch (error) {
            console.log(error)
        }

    };

    useEffect(() => {
        getData();
    }, []);


    const { name, customId } = selectedUserInfo || {};
    if (!name || !customId) {
        return <div>Loading selectedUserInfo...</div>
    }

    const handleTransaction = (category) => {
        return (
            transaction && transaction.map((trans, index) => {
                if (trans.userNameCustomId === selectedUserInfo.customId) {
                    if (trans.category === category) {
                        const bodyInfo = [
                            { label: 'type', value: trans.type },
                            { label: 'category', value: trans.category },
                            { label: 'description', value: trans.description },
                        ];
                        return (
                            <Accordion>
                                <Accordion.Item key={trans.customId.toString()} eventKey={trans.customId.toString()}>
                                    <Accordion.Header>
                                        <Col key={trans.customId.toString()+index} className="p-0 d-flex justify-content-center" xs={{ span: 8, offset: 0 }}>
                                            {trans.title}
                                        </Col>
                                        <Col key={trans.customId.toString()+index} className="p-0 d-flex justify-content-center" xs={{ span: 1, offset: 0 }}>
                                            |
                                        </Col>
                                        <Col key={trans.customId.toString()+index} className="p-0 d-flex justify-content-center" xs={{ span: 3, offset: 0 }}>
                                            {trans.currency} {trans.amount}
                                        </Col>
                                    </Accordion.Header>
                                    <Accordion.Body>
                                        {bodyInfo.map((info,innerIndex) => (
                                            <Row key={trans.customId.toString()}>
                                                <Col key={trans.customId+1} className="p-0 d-flex justify-content-end" xs={{ span: 3, offset: 0 }}>
                                                    {info.label}
                                                </Col>
                                                <Col key={trans.customId+2} className="p-0 d-flex justify-content-center" xs={{ span: 1, offset: 0 }}>
                                                    :
                                                </Col>
                                                <Col key={trans.customId+3} className="p-0 d-flex justify-content-start" xs={{ span: 8, offset: 0 }}>
                                                    {info.value}
                                                </Col>
                                            </Row>
                                        ))}
                                    </Accordion.Body>
                                </Accordion.Item>
                            </Accordion>
                        )
                    }
                }
            })
        )
    }

    return (
        <Container fluid>
            <Row>
                <Col xs={12}><center><h1>{name}</h1></center></Col>
            </Row>
            <div className="hl"></div>

            <div className="user-transaction-content">
                <section className="left-section">
                    <Row className="UserTransactionTitles">
                        <Col xs={10}>
                            <h1>Debts</h1>
                        </Col>
                        <Col xs={2}>
                            <AddButton divClassName={'divContainerAddButtonTransaction'} linkToRoute={'/userTransaction/userTransactionForm'} propsToPass={{ category: 'Debts', userNameCustomId: customId, userName: name }} />
                        </Col>
                    </Row>
                    <Row>
                        {handleTransaction('Debts')}
                    </Row>
                </section>
                <div className="vl"></div>
                <section className="right-section">
                    <Row className="UserTransactionTitles">
                        <Col xs={10}>
                            <h1>Repayments</h1>
                        </Col>
                        <Col xs={2}>
                            <AddButton divClassName={'divContainerAddButtonTransaction'} linkToRoute={'/userTransaction/userTransactionForm'} propsToPass={{ category: 'Repayments', userNameCustomId: customId, userName: name }} />
                        </Col>
                    </Row>
                    <Row>
                        {handleTransaction('Repayments')}
                    </Row>
                </section>

            </div>
        </Container>

    );
}
