import { useEffect } from 'react';
import Col from 'react-bootstrap/Col';
import Container from 'react-bootstrap/Container';
import Image from 'react-bootstrap/Image';
import Row from 'react-bootstrap/Row';
import { useDispatch, useSelector } from 'react-redux';
import { addUserInfo } from '../features/user/userSlice';
import { Button } from 'react-bootstrap';
import '../styles.css'


export function MainContainer() {
    const { userInfo } = useSelector((store) => store.user)
    return (
        <Container>
            <Row>
                {userInfo.map((user) => (
                    <Col key={user.id} xs={6} md={4} style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
                        <div className='userImageContainer'>
                            <Image
                                src={user.path}
                                alt={user.name}
                                roundedCircle
                                style={{ position: 'absolute', top: 0, left: 0, width: '100%', height: '100%' }}
                            />
                        </div>
                    </Col>
                ))}
                <Col xs={6} md={4} style={{ display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
                    <Button className='userImageContainer' variant="primary">
                        <Image  src="AddButton.png" alt="Add Button" roundedCircle style={{ position: 'absolute', top: 0, left: 0, width: '100%', height: '100%' }} />
                        Add User
                    </Button>
                </Col>
            </Row>
        </Container>
    )
}
