import React from 'react';
import { BarChart } from './BarChart';

const RenderBarChart = ({ totalDebt, remainingDebt }) => {
    const data = {
        labels: ['Debt'],
        datasets: [
            {
                label: 'Remaining Debt',
                data: [remainingDebt],
                // backgroundColor: 'rgba(255, 99, 132, 0.5)',
                backgroundColor: 'blue',
                borderWidth: 1,
            },
            {
                label: 'Total Debt',
                data: [totalDebt],
                // backgroundColor: 'rgba(54, 162, 235, 0.5)',
                backgroundColor: 'red',
                borderWidth: 1,
            },
        ],
    };

    const options = {
        indexAxis: 'x',
        plugins: {
            legend: {
                display: false,
                // position: 'bottom',
            },
        },
        scales: {
            x: {
                stacked: true,
            },
            y: {
                stacked: true,
            },
        },
        layout: {
            padding: {
                top: 20, // Increase top padding to make the chart taller
            },
        },
        // responsive: true,
        interaction: {
            mode: 'index',
            intersect: false
        },
    };

    return <BarChart data={data} options={options} />;
};

export default RenderBarChart;
